mapper.select:
SELECT uod.order_detail_number, c.customer_name, uo.receiver_name, uo.receiver_tel, p.p_name, (product_count * product_price) total_price, uo.order_date, uod.order_check FROM u_order uo INNER JOIN u_o_detail uod ON uo.order_no= uod.order_no INNER JOIN customer c ON uo.customer_no = c.customer_no INNER JOIN product p ON uod.p_no = p.p_no
${searchCondition} LIKE '%${searchKeyword}%'
order_date BETWEEN #{startDate} AND #{endDate}

mapper.update:
update u_o_detail 
set order_check= 'Y'
where order_detail_number = #{orderDetailNumber}
