mapper.update:
update product 
set p_count = p_count - #{productCount} 
where p_no = #{pNo}

INSERT INTO u_o_detail( order_detail_number, order_no, p_no, product_count, product_price, order_check, refund_check ) SELECT o_d_no_seq.nextval as order_detail_number, T.* FROM(
SELECT (select max(order_no) from u_order) as order_no, #{item.pNo} as p_no, #{item.productCount} as product_count, #{item.productPrice} as product_price, 'N' as order_check, 'N' as refund_check FROM sys.dual
) T

update.foreach:
update product 
set p_count = p_count - #{item.productCount} 

	p_no = #{item.pNo}
