mapper.insert:
INSERT ALL INTO product(p_no, p_price, p_name, p_detail, p_count, p_date, p_color, category_code) VALUES(product_no_seq.nextval, #{pPrice}, #{pName}, #{pDetail}, #{pCount}, sysdate, #{pColor}, #{categoryCode}) INTO p_image(file_number,p_no
, origin_file_name, stored_file_name, file_size
) VALUES(file_no_seq.nextval, product_no_seq.nextval
, #{originFileName, jdbcType=VARCHAR}, #{storedFileName, jdbcType=VARCHAR}, #{fileSize, jdbcType=VARCHAR}
) SELECT * FROM dual

mapper.select:
		SELECT D_CATEGORY_CODE, nvl(D_NAME,'a') D_NAME
		FROM P_D_CATEGORY
		WHERE category_code = #{categoryCode}
